custom_css:
/* Example CSS for your font: kaesmasyi-regular */

/* Apply to all text */
:root * {
	--font-default: 'kaesmasyi-regular';
	--default-font: 'kaesmasyi-regular';
	--font-family-editor: 'kaesmasyi-regular';
	--font-interface-override: 'kaesmasyi-regular';
	--font-text-override: 'kaesmasyi-regular';
}

/* Example: Apply to custom CSS class */
.custom-font * {
	font-family: 'kaesmasyi-regular' !important;
}

/* Example: Apply to specific elements only */
.custom-font h1, .custom-font h2, .custom-font h3 {
	font-family: 'kaesmasyi-regular' !important;
}
